UPDATE creature_ai_texts SET content_loc5='我感到前方有危險。' WHERE entry=-492; /* content: I sense danger up ahead. */
UPDATE creature_ai_texts SET content_loc5='我的爪子將把你撕得粉碎，$R。' WHERE entry=-494; /* content: My talons will shred your puny body, $r. */
UPDATE creature_ai_texts SET content_loc5='我猜你帶來了好消息?' WHERE entry=-502; /* content: I presume you come with good news? */
