UPDATE creature_ai_texts SET content_loc5='誰敢破壞埃德薩拉斯的聖潔?懦夫，面對我!' WHERE entry=-553; /* content: Who dares disrupt the sanctity of Eldre\'Thalas? Face me, cowards! */
UPDATE creature_ai_texts SET content_loc5='我的靈魂終於可以安息了...哦，親愛的塞瑞利恩...' WHERE entry=-554; /* content: Finally, my soul may rest... Oh, dearest Cerellean... */
UPDATE creature_ai_texts SET content_loc5='哭著乞求憐憫吧!不久之後你們將喪失無意義的生命!' WHERE entry=-570; /* content: Cry for mercy! Your meaningless lives will soon be forfeit! */
UPDATE creature_ai_texts SET content_loc5='我會讓你痛苦的死去。' WHERE entry=-571; /* content: Your death will be a painful one. */
UPDATE creature_ai_texts SET content_loc5='你...被標記了。' WHERE entry=-572; /* content: You... are marked. */
UPDATE creature_ai_texts SET content_loc5='可悲的傢伙!' WHERE entry=-574; /* content: Miserable nuisance! */
UPDATE creature_ai_texts SET content_loc5='(惡魔語)交出你的靈魂吧!' WHERE entry=-575; /* content: Shaza-Kiel! */
UPDATE creature_ai_texts SET content_loc5='以索瑞森鬍鬚之名!殺了他們!' WHERE entry=-576; /* content: By Thaurissan\'s beard! Slay them! */
UPDATE creature_ai_texts SET content_loc5='我，格瑞姆洛克，是個國王!' WHERE entry=-577; /* content: Me Grimlok, king! */
